import React, { useState } from "react";
import './NewFlashcardForm.css';

function NewFlashcardForm({ onAddFlashcard, categories, onAddCategory }) {
    const [frontText, setFrontText] = useState('');
    const [backText, setBackText] = useState('');
    const [selectedCategory, setSelectedCategory] = useState(categories[0]);
    const [newCategory, setNewCategory] = useState('');

    const handleSubmitCard = (e) => {
        e.preventDefault();
        if (frontText.trim() && backText.trim()) {
            onAddFlashcard({ frontText, backText, selectedCategory });
            setFrontText('');
            setBackText('');
            setSelectedCategory(categories[0]);
        }

    }

    const handleSubmitCategory = (e) => {
        e.preventDefault();
        if (newCategory.trim()) {
            onAddCategory({ newCategory });
            setSelectedCategory(newCategory);
            setNewCategory('');
        }
    }

    return (
        <form className="new-flashcard-form" onSubmit={handleSubmitCard}>
            <input
                type="text"
                placeholder="Word"
                value={frontText}
                onChange={(e) => setFrontText(e.target.value)}
                required
            />
            <input
                type="text"
                placeholder="Term"
                value={backText}
                onChange={(e) => setBackText(e.target.value)}
                required
            />
            <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
                {categories.map((category) => (
                    <option key={category} value={category}>
                        {category}
                    </option>
                ))}
            </select>
            <input
                type="text"
                placeholder="New category"
                value={newCategory}
                onChange={(e) => setNewCategory(e.target.value)} />
            <button onClick={handleSubmitCategory}>Add category</button>
            <button type="submit">Add card</button>
        </form>
    );
};

export default NewFlashcardForm;
