import React, { useState } from 'react';
import './App.css';
import FlashcardList from './components/FlashcardList/FlashcardList';
import NewFlashcardForm from './components/NewFlashcardForm/NewFlashcardForm';

function App() {
    const [flashcards, setFlashcards] = useState([
        { frontText: 'Hello', backText: 'Привет' },
        { frontText: 'Goodbye', backText: 'До свидания' },
        { frontText: 'Please', backText: 'Пожалуйста' },
        { frontText: 'Thank you', backText: 'Спасибо' },
    ]);
    const addFlashcard = (NewFlashcard) => {
        setFlashcards([...flashcards, NewFlashcard])
    }
    return (
        <div className="App">
            <h1>Word Wielders</h1>
            <NewFlashcardForm onAddFlashcard={addFlashcard} />
            <FlashcardList flashcards={flashcards} />
        </div>
    );
}

export default App;
