import React, { useState } from 'react';
import './App.css';
import FlashcardList from './components/FlashcardList/FlashcardList';
import NewFlashcardForm from './components/NewFlashcardForm/NewFlashcardForm';

function App() {
    const [flashcards, setFlashcards] = useState([
        { id: 1, frontText: 'Hi', backText: 'Привет' },
        { id: 2, frontText: 'Goodbye', backText: 'Пока' },
        { id: 3, frontText: 'Please', backText: 'Пожалуйста' },
        { id: 4, frontText: 'Thank you', backText: 'Спасибо' },
    ]);
    const addFlashcard = (NewFlashcard) => {
        setFlashcards([
            ...flashcards,
            { NewFlashcard, id: Date.now() }
        ]);
    };

    return (
        <div className="App">
            <h1>Word Wielders</h1>
            <NewFlashcardForm onAddFlashcard={addFlashcard} />
            <FlashcardList flashcards={flashcards} />
        </div>
    );
}

export default App;
